import './App.css';
import React, { useState } from 'react';
import game_data from "./game_data.json";
import CreatureImg from './CreatureImage';

function Menu() {

  const [game_data, setData] = React.useState(null);

  React.useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => setData(data.message));
  }, []);

  React.useEffect(() => {
    fetch("/blobs")
      .then((res) => res.json())
      .then((data) => setData(data));
  }, []);

  return (
    <>
    <h1> blob fight</h1>
    <p> choose your blob</p>
    <p> {data} </p>
    <div className="App">
      {Object.keys(game_data.creatures).map((creature) => {
        console.log(creature);
        return (<button class = "icon">
          <CreatureImg  name = {creature}/> <p>{creature}</p></button>);
      })}
    </div>
    </>
  );
}

export default Menu;
