import Creature from './Creature';
import EnemyCreature from './EnemyCreature';
import './App.css';
import React, { useState } from 'react';
import game_data from "./game_data.json";

function App(props) {


  const [game_data, getGameData] = useState({});
  const [turn, setTurn] = useState(0);
  const [myCreature, setCreature] = useState(game_data.creatures["erik"]);
  const [enemyCreature, setEnemyCreature] = useState(game_data.creatures["bobby"]);

  const [lastAction, setLastAction] = useState("Let the game begin!");

  function doTurn(moveName) {
    if(enemyCreature.speed < myCreature.speed) {
      makeMove(moveName, true);
      makeMove(enemyCreature.moves[Math.floor(Math.random()*enemyCreature.moves.length)], false);
    } else {
      makeMove(enemyCreature.moves[Math.floor(Math.random()*enemyCreature.moves.length)], false);
      makeMove(moveName, true);
    }
    setTurn(turn + 1);
  }

  function XOR(a,b) {
    return ( a || b ) && !( a && b );
  }

  function makeMove(moveName, myMove) {
    console.log(moveName, myMove);
    let move = game_data.moves[moveName];
    if(myMove) {
      setLastAction(lastAction + " " + myCreature.name + " performed " + move.display_name);
    } else {
      setLastAction(lastAction + " " + enemyCreature.name + " performed " + move.display_name);
    }
    let target = XOR(myMove,move.effect.targetEnemy) ? myCreature : enemyCreature;
    let changeFunc =  XOR(myMove,move.effect.targetEnemy) ? setCreature : setEnemyCreature;

    Object.keys(move.effect).forEach((key) => {
      if(key != "targetEnemy") {
        changeFunc({...target, [key] : target[key] + move.effect[key]})
      }
    });
  }


  return (
    <>
    <h1> blob fight</h1>
    <p>{lastAction}</p>
    <p> Turn : {turn}</p>
    <div className="App">
      <EnemyCreature data = {enemyCreature}/>
      <Creature doTurn = {doTurn} data = {myCreature}/>
    </div>
    </>
  );
}

export default App;
