import game_data from "./game_data.json";
import './App.css';


function getPath() {
  var x = Math.trunc(Math.random());
    var y = Math.trunc(Math.random());
    var xstep = 0;
    var ystep = 0;
    var path = '0,0';
    for (k = 0; k < 3 ; k++) {
          path += ' ';
          path += (x).toString();
          path += ',';
          path += (y).toString();
          xstep = Math.random();
          ystep = Math.random();
          xstep *= 10;
          ystep *= 10;
          x += Math.trunc(xstep);
          y += Math.trunc(ystep);
        }
        return path;
}

function CreatureImg(props) {

  console.log(props);
  console.log(game_data);
  console.log(game_data.creatures[props.name]);

  return (
    <svg width = "100px" height = "100px" id="sw-js-blob-svg" viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg" version="1.1">
      <defs>                         
        <linearGradient id="sw-gradient" x1="0" x2="1" y1="1" y2="0"> 
        <stop id="stop1" stopColor="rgba(248, 117, 55, 1)" offset="0%"></stop>  
        <stop id="stop2" stopColor="rgba(251, 168, 31, 1)" offset="100%"></stop>
        </linearGradient>
      </defs>                
      <path fill="url(#sw-gradient)" d={getPath()} transform="translate(50 50)" strokeWidth="0"></path>             
  </svg>
  );
}

export default CreatureImg;
