import './App.css';
import React, { useState } from 'react';
import App from './App';
import Menu from './Menu'; 
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { socket } from './socket';

function Header() {


  const [game, setGame] = useState(null);
  const [socketRef, setSocket] = useState(null);

  socket.on("connect", () => {
    setSocket(socket);
  });

  return (
    <BrowserRouter>
        <Routes>
            <Route path="/" element={<Menu socket = {socketRef} game = {game} setGame = {setGame}/>} />
            <Route path="/game" element={<App socket = {socketRef} game = {game}/>} />
        </Routes>
    </BrowserRouter>
  )

}

export default Header;
