import Creature from './Creature';
import EnemyCreature from './EnemyCreature';
import './App.css';
import React, { useState } from 'react';
import game_data from "./game_data.json";

function App() {

  const [turn, setTurn] = useState(0);
  const [myCreature, setCreature] = useState(game_data.creatures["erik"]);
  const [enemyCreature, setEnemyCreature] = useState(game_data.creatures["bobby"]);

  const [lastAction, setLastAction] = useState("Let the game begin!");

  function doTurn(moveName) {
    if(enemyCreature.speed < myCreature.speed) {
      makeMove(moveName, true);
      makeMove(enemyCreature.moves[Math.floor(Math.random()*enemyCreature.moves.length)], false);
    } else {
      makeMove(enemyCreature.moves[Math.floor(Math.random()*enemyCreature.moves.length)], false);
      makeMove(moveName, true);
    }
  }

  function makeMove(moveName, myMove) {
    let move = game_data.moves[moveName];
    if(myMove) {
      setLastAction(myCreature.name + " performed " + move.display_name);
      if(move.dmg) {
        setEnemyCreature({...enemyCreature, hp: enemyCreature.hp -= Math.max(move.dmg - enemyCreature.defense + myCreature.attack,0)});
      } else {
        Object.keys(move).forEach(key => {
          console.log({... myCreature, key: myCreature[key] + move[key]})
          setCreature({... myCreature, key: myCreature[key] + move[key]});
        });
      }
    } else {
      setLastAction(enemyCreature.name + " performed " + move.display_name);
      if(move.dmg) {
        setCreature({...myCreature, hp: myCreature.hp - Math.max(move.dmg - myCreature.defense + enemyCreature.attack,0)});
      }else {
        Object.keys(move).forEach(key => {
          setEnemyCreature({... enemyCreature, key: enemyCreature[key] + move[key]});
        });
      }
    }
  }


  return (
    <div className="App">
      <p>{lastAction}</p>
      <EnemyCreature data = {enemyCreature}/>
      <Creature doTurn = {doTurn} data = {myCreature}/>
    </div>
  );
}

export default App;
