import Creature from './Creature';
import EnemyCreature from './EnemyCreature';
import './App.css';
import React, { useState } from 'react';
import game_data from "./game_data.json";
import CreatureImg from './CreatureImage';

function Menu() {






  return (
    <>
    <h1> blob fight</h1>
    <p> choose your blob</p>
    <div className="App">
      game_data.creatures.map((creature) => {
        <CreatureImg name = {creature.name}/>
      })
    </div>
    </>
  );
}

export default Menu;
