import './App.css';
import React, { useState, useEffect } from 'react';
import CreatureImg from './CreatureImage';
import io from 'socket.io-client';


function Menu() {

  const [game_data, setData] = React.useState(null);

  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const newSocket = io();
    setSocket(newSocket);
    return () => newSocket.close();
  }, [setSocket]);


  function reqGame(name) {
    fetch('/join_game', {
      method: 'post',
      headers: {'Content-Type':'application/json'},
      body: {
       "name": name
      }
     });
  }

  React.useEffect(() => {
    fetch("/blobs")
      .then((res) => res.json())
      .then((data) => setData(data));
  }, []);

  return (
    <>
    <h1> blob fight</h1>
    <p> choose your blob</p>
    <div className="App">
      {game_data ? Object.keys(game_data.creatures).map((creature) => {
        return (<button onClick = {() => {reqGame(creature)}}class = "icon">
          <CreatureImg  name = {creature}/> <p>{creature}</p></button>);
      }) : "Loading..."}
    </div>
    </>
  );
}

export default Menu;
