import logo from './logo.svg';
import game_data from "./game_data.json";
import './App.css';
import CreatureImg from './CreatureImage';

function Creature(props) {

  // console.log(props);
  // console.log(game_data);
  // console.log(game_data.creatures[props.name]);
  console.log(game_data.creatures[props.name].moves);

  return (
    <div className="Creature">
        <CreatureImg name = {props.name}/>
        <h2>
          {game_data.creatures[props.name].name}
        </h2>
        <p>
          hp: {game_data.creatures[props.name].hp}
        </p>
        <p>
          defense: {game_data.creatures[props.name].defense}
        </p>
        <p>
          attack: {game_data.creatures[props.name].attack}
        </p>
        <p>
          speed: {game_data.creatures[props.name].speed}
        </p> 
        {game_data.creatures[props.name].moves.map((move,i) => {
          return (<button>{game_data.moves[move].display_name} {game_data.moves[move].pp}</button>);
        })}   
    </div>
  );
}

export default Creature;
