import Creature from './Creature';
import EnemyCreature from './EnemyCreature';
import './App.css';
import React, { useState } from 'react';
import game_data from "./game_data.json";
import { socket } from './socket';

function App(props) {


  const [game_data, setGameData] = useState(null);

  React.useEffect(() => {
    console.log('sending');
    fetch(`/game?id=${props.game}`)
      .then((res) => res.json())
      .then((game_data) => setGameData(game_data));
  }, []);

  console.log(game_data);

  return (
    <>
    <h1> blob fight</h1>
    {game_data ? <>
      {game_data.log.map((l) => {
        <p>{l}</p>
      })}
    <p> Turn : {game_data.turn}</p>
    <div className="App">
      <EnemyCreature data = {game_data.creatures[Object.keys(game_data.creature).filter((k) => k != props.socket.id)[0]]}/>
      <Creature data = {game_data.creatures[props.socket.id]}/>
    </div>
    </>: "Loading..."}

    </>
  );
}

export default App;
